import { editableInputTypes } from '@testing-library/user-event/dist/utils';
import React,{useState} from 'react'
import "./App.css"
import data from "./mock-data.json"
 import {nanoid} from 'nanoid';

export default function App() {

 const[contacts,setcontacts]=useState(data);
 const[addformdata,setaddformdata]=useState({
  Accountname:'',
  CreditAmount:'',
  DebitAmount:''
 })
 const[editcontactid,setcontactid]=useState(null);

 const handleformchange=(event)=>{
  event.preventDefault();
  //to get the name of the input that user has changeed
  const fieldname = event.target.getAttribute("name");

  //to get actural value of that input
  const fieldvalue=event.target.value;

  const newfordata={...addformdata};
  newfordata[fieldname]=fieldvalue;
  setaddformdata(newfordata);

 };
 //when form is submitted this is called
 const handleformsubmit=(event)=>{
  event.preventDefault();
  const newcontact={
    //id beacaue it will help i identifying which contact is added and whichis deleted
    id:nanoid(),
    Accountname:addformdata.Accountname,
    CreditAmount:addformdata.CreditAmount,
    DebitAmount:addformdata.DebitAmount
  }
  //creating new array to avoid mutation
  const newcontacts=[...contacts,newcontact];
  setcontacts(newcontacts);

 }

 const handledelete =(contactid)=>{

  const newcontacts=[...contacts];
  const index=contacts.findIndex((contact)=>contact.id=contactid);
  newcontacts.slice(index,1);
  setcontacts(newcontacts);
 }

  return (
    <div className='app-container'>
      <table>
        <thead>
          <tr>
            <th>Account</th>
            <th>Credit</th>
            <th>Debit</th>
          </tr>
        </thead>
        <tbody>
          {contacts.map((contact)=>(

            <tr>
            <td>{contact.Accountname}</td>
            <td>{contact.CreditAmount}</td>
            <td>{contact.DebitAmount}</td>
            </tr>
          ))}

        </tbody>
      </table>
      <h2>Add a Account</h2>
      <form onSubmit={handleformsubmit}>
        <input type="text" name="Accountname" placeholder='Account' onChange={handleformchange}/>
        <input type="number" name="CreditAmount" placeholder='Credit' onChange={handleformchange}/>
        <input type="number" name="DebitAmount" placeholder='Debit' onChange={handleformchange}/>
        <button type='submit'>Add</button>
        <button type='button' onClick={handledelete}>delete</button>
      </form>

    </div>
  )
}
